        },
        getRandomColor() {
            const letters = '0123456789ABCDEF';
            let color = '#';
            for (let i = 0; i < 6; i++) {
                color += letters[Math.floor(Math.random() * 16)];
            }
            return color;
        },
        addValueToNodes(nodes) {
            for (let node of nodes) {
                // 给当前节点添加值
                node.label = {
                    color: this.getRandomColor()
                };

                // 如果当前节点有子节点，递归地给子节点添加值
                if (node.children) {
                    this.addValueToNodes(node.children);
                }
            }
        }
    },
    mounted() {

        this.drawPie();

    }
}
</script>

